import { ArrowBack, ArrowForward } from '@material-ui/icons';
import React, { useState } from 'react';
import { SliderData } from './SliderData';
import "./ImageSlider.css";
import LandingPageCards from './LandingPageCards';

const ImageSlider = ({ slides }) => {
  const [current, setCurrent] = useState(0);
  const length = slides.length;

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  if (!Array.isArray(slides) || slides.length <= 0) {
    return null;
  }

  return (
    <section className='slider_1'>
      <ArrowBack className='left-arrow' onClick={prevSlide} />
      <ArrowForward className='right-arrow' onClick={nextSlide} />
      {SliderData.map((slide, index) => {
        return (
          <div
            className={index === current ? 'slide_1 active_1' : 'slide_1'}
            key={index}
          >
            {index === current && (
              <LandingPageCards src={slide.image}/>
            )}
          </div>
        );
      })}
    </section>
  );
};

export default ImageSlider;
