import React from "react";
import SortByContent from "./SortByContent";

function ViewProperty() {
        const [showBasic, setShowBasic] = useState(false);
        function resizeHeaderOnScroll() {
          const distanceY = window.pageYOffset || 
          document.documentElement.scrollTop,
          shrinkOn = 5,
          headerEl = document.getElementById('js-header');
         
         if (distanceY > shrinkOn) {
            headerEl.classList.add("smaller");
            } else {
             headerEl.classList.remove("smaller");
            }
          }
         
         window.addEventListener('scroll', resizeHeaderOnScroll);
  return (
    <div className="view_property">
      <div className="property_hero">
        <div className="property_hero_flex">
          <div className="property_hero_content">
            <h2>Property on Lorem Ave</h2>
          </div>
        </div>
      </div>
      <SortByContent />
      <div className="showcase_top_header">
        <div className="sth_left">
          <p>Home</p>
          <h1>Home on Lorem Ave</h1>
          <p className="property_address">Lorem Ave, Miami, FL 12345, USA</p>
        </div>

        <div className="sth_right">
          <p>For Sale</p>
          <p className="property_price">$500,000</p>
        </div>
      </div>
    </div>
  );
}

export default ViewProperty;
